@prefix cdifd: <https://cdif.org/validation/0.1/shacl#> .
@prefix schema1: <http://schema.org/> .
@prefix sh: <http://www.w3.org/ns/shacl#> .
@prefix xsd: <http://www.w3.org/2001/XMLSchema#> .

[] a sh:ValidationReport ;
    sh:conforms false ;
    sh:result [ a sh:ValidationResult ;
            sh:focusNode <https://example.org/PersonExample_zZc> ;
            sh:resultMessage "A person must have a name or an identifier provided; affiliation to an organization is recommended." ;
            sh:resultSeverity sh:Violation ;
            sh:sourceConstraintComponent sh:OrConstraintComponent ;
            sh:sourceShape cdifd:CDIFPersonShape ;
            sh:value <https://example.org/PersonExample_zZc> ],
        [ a sh:ValidationResult ;
            sh:focusNode <https://example.org/PersonExample_zZc> ;
            sh:resultMessage "Optional: an affiliation must have object schema:Organization" ;
            sh:resultPath schema1:affiliation ;
            sh:resultSeverity sh:Warning ;
            sh:sourceConstraintComponent sh:OrConstraintComponent ;
            sh:sourceShape cdifd:affiliationProperty ;
            sh:value "some organization, schema TBD" ],
        [ a sh:ValidationResult ;
            sh:focusNode <https://example.org/PersonExample_zZc> ;
            sh:resultMessage "Less than 1 values on ex:PersonExample_zZc->schema1:contactPoint" ;
            sh:resultPath schema1:contactPoint ;
            sh:resultSeverity sh:Violation ;
            sh:sourceConstraintComponent sh:MinCountConstraintComponent ;
            sh:sourceShape cdifd:contactPointProperty ] .

