@prefix schema1: <http://schema.org/> .
@prefix sh: <http://www.w3.org/ns/shacl#> .
@prefix xsd: <http://www.w3.org/2001/XMLSchema#> .

[] a sh:ValidationReport ;
    sh:conforms false ;
    sh:result [ a sh:ValidationResult ;
            sh:focusNode <https://example.org/cdifOptional_23578> ;
            sh:resultMessage "A dataset should have keywords (schema:keywords) for discoverability." ;
            sh:resultPath schema1:keywords ;
            sh:resultSeverity sh:Warning ;
            sh:sourceConstraintComponent sh:MinCountConstraintComponent ;
            sh:sourceShape [ sh:message "A dataset should have keywords (schema:keywords) for discoverability." ;
                    sh:minCount 1 ;
                    sh:path schema1:keywords ;
                    sh:severity sh:Warning ] ],
        [ a sh:ValidationResult ;
            sh:focusNode <https://example.org/cdifOptional_23578> ;
            sh:resultMessage "A dataset should identify its creator(s) (schema:creator)." ;
            sh:resultPath schema1:creator ;
            sh:resultSeverity sh:Warning ;
            sh:sourceConstraintComponent sh:MinCountConstraintComponent ;
            sh:sourceShape [ sh:message "A dataset should identify its creator(s) (schema:creator)." ;
                    sh:minCount 1 ;
                    sh:path schema1:creator ;
                    sh:severity sh:Warning ] ] .

