    sh:result [ a sh:ValidationResult ;
            sh:focusNode [ a schema1:Dataset,
                        schema1:Product ;
                    schema1:additionalType "Analysis Advanced Imaging and Visualization of Astromaterials (AIVA)",
                        "ada:DataDeliveryPackage" ;
