                    schema1:name "AIVA Analysis of Sample" ] ;
            sh:resultMessage "AIVA products must have a schema:additionalType matching a valid AIVA type." ;
            sh:resultPath schema1:additionalType ;
            sh:resultSeverity sh:Violation ;
            sh:sourceConstraintComponent sh:OrConstraintComponent ;
            sh:sourceShape [ sh:message "AIVA products must have a schema:additionalType matching a valid AIVA type." ;
                    sh:minCount 1 ;
                    sh:or ( [ sh:hasValue "Analysis Advanced Imaging and Visualization of Astromaterials (AIVA)" ] [ sh:hasValue "Advanced Imaging and Visualization of Astromaterials" ] ) ;
                    sh:path schema1:additionalType ] ;
            sh:value "ada:DataDeliveryPackage" ] .

