[] a sh:ValidationReport ;
    sh:conforms false ;
    sh:result [ a sh:ValidationResult ;
            sh:focusNode [ a schema1:Dataset,
                        schema1:Product ;
                    schema1:additionalType "Elemental Analysis-Isotope Ratio Mass Spectrometry (EA-IRMS)",
                        "ada:DataDeliveryPackage" ;
                    schema1:description "Elemental Analysis - Isotope Ratio Mass Spectrometry (EA-IRMS) data" ;
                    schema1:name "EA-IRMS Analysis of Sample" ] ;
            sh:resultMessage "EA-IRMS products must have a schema:additionalType matching a valid EA-IRMS type." ;
            sh:resultPath schema1:additionalType ;
            sh:resultSeverity sh:Violation ;
            sh:sourceConstraintComponent sh:OrConstraintComponent ;
            sh:sourceShape [ sh:message "EA-IRMS products must have a schema:additionalType matching a valid EA-IRMS type." ;
                    sh:minCount 1 ;
                    sh:or ( [ sh:hasValue "Elemental Analysis-Isotope Ratio Mass Spectrometry (EA-IRMS)" ] [ sh:hasValue "Elemental analysis - isotope ratio mass spectrometry" ] ) ;
                    sh:path schema1:additionalType ] ;
            sh:value "ada:DataDeliveryPackage" ] .

