[] a sh:ValidationReport ;
    sh:conforms false ;
    sh:result [ a sh:ValidationResult ;
            sh:focusNode [ a schema1:Person ;
                    schema1:affiliation [ a schema1:Organization ;
                            schema1:name "NASA Johnson Space Center" ] ;
                    schema1:identifier "https://orcid.org/0000-0002-9876-5432" ;
                    schema1:name "Researcher, John Q." ] ;
            sh:resultMessage "Less than 1 values on [ rdf:type schema1:Person ; schema1:affiliation [ rdf:type schema1:Organization ; schema1:name Literal(\"NASA Johnson Space Center\") ] ; schema1:identifier Literal(\"https://orcid.org/0000-0002-9876-5432\") ; schema1:name Literal(\"Researcher, John Q.\") ]->schema1:contactPoint" ;
            sh:resultPath schema1:contactPoint ;
            sh:resultSeverity sh:Violation ;
            sh:sourceConstraintComponent sh:MinCountConstraintComponent ;
            sh:sourceShape cdifd:contactPointProperty ] .

