[] a sh:ValidationReport ;
    sh:conforms false ;
    sh:result [ a sh:ValidationResult ;
            sh:focusNode [ a schema1:Dataset,
                        schema1:Product ;
                    schema1:additionalType "Secondary Ion Mass Spectrometry (SIMS) Tabular",
                        "ada:DataDeliveryPackage" ;
                    schema1:description "Secondary Ion Mass Spectrometry (SIMS) data" ;
                    schema1:name "SIMS Analysis of Sample" ] ;
            sh:resultMessage "SIMS products must have a schema:additionalType matching a valid SIMS type." ;
            sh:resultPath schema1:additionalType ;
            sh:resultSeverity sh:Violation ;
            sh:sourceConstraintComponent sh:OrConstraintComponent ;
            sh:sourceShape [ sh:message "SIMS products must have a schema:additionalType matching a valid SIMS type." ;
                    sh:minCount 1 ;
                    sh:or ( [ sh:hasValue "Secondary Ion Mass Spectrometry (SIMS) Tabular" ] [ sh:hasValue "Secondary ion mass spectrometry" ] ) ;
                    sh:path schema1:additionalType ] ;
            sh:value "ada:DataDeliveryPackage" ] .

