[] a sh:ValidationReport ;
    sh:conforms false ;
    sh:result [ a sh:ValidationResult ;
            sh:focusNode [ a schema1:Dataset,
                        schema1:Product ;
                    schema1:additionalType "Fluorescence Microscopy (UVFM) Image",
                        "ada:DataDeliveryPackage" ;
                    schema1:description "Ultraviolet Fluorescence Microscopy (UVFM) data" ;
                    schema1:name "UVFM Analysis of Sample" ] ;
            sh:resultMessage "UVFM products must have a schema:additionalType matching a valid UVFM type." ;
            sh:resultPath schema1:additionalType ;
            sh:resultSeverity sh:Violation ;
            sh:sourceConstraintComponent sh:OrConstraintComponent ;
            sh:sourceShape [ sh:message "UVFM products must have a schema:additionalType matching a valid UVFM type." ;
                    sh:minCount 1 ;
                    sh:or ( [ sh:hasValue "Fluorescence Microscopy (UVFM) Image" ] [ sh:hasValue "UV Fluorescence Microscopy" ] ) ;
                    sh:path schema1:additionalType ] ;
            sh:value "ada:DataDeliveryPackage" ] .

