                    sh:or ( [ sh:hasValue "Visible, near-infrared, and mid-infrared Spectroscopy (VNMIR) Point" ] [ sh:hasValue "Visible, near-infrared, and mid-infrared Spectroscopy (VNMIR) Overview Image" ] [ sh:hasValue "Visible, near-infrared, and mid-infrared Spectroscopy (VNMIR) Spectral Map" ] [ sh:hasValue "Visible, near-, and mid-infrared spectroscopy" ] ) ;
