[] a sh:ValidationReport ;
    sh:conforms false ;
    sh:result [ a sh:ValidationResult ;
            sh:focusNode [ a schema1:Dataset,
                        schema1:Product ;
                    schema1:additionalType "X-ray Absorption Near Edge Structure Hyperspectral Image Stack (XANES)",
                        "ada:DataDeliveryPackage" ;
                    schema1:description "X-ray Absorption Near Edge Structure (XANES) data" ;
                    schema1:name "XANES Analysis of Sample" ] ;
            sh:resultMessage "XANES products must have a schema:additionalType matching a valid XANES type." ;
            sh:resultPath schema1:additionalType ;
            sh:resultSeverity sh:Violation ;
            sh:sourceConstraintComponent sh:OrConstraintComponent ;
            sh:sourceShape [ sh:message "XANES products must have a schema:additionalType matching a valid XANES type." ;
                    sh:minCount 1 ;
                    sh:or ( [ sh:hasValue "X-ray Absorption Near Edge Structure Hyperspectral Image Stack (XANES)" ] [ sh:hasValue "X-ray absorption near edge structure (XANES) spectroscopy" ] ) ;
                    sh:path schema1:additionalType ] ;
            sh:value "ada:DataDeliveryPackage" ] .

