[] a sh:ValidationReport ;
    sh:conforms false ;
    sh:result [ a sh:ValidationResult ;
            sh:focusNode [ a schema1:Event,
                        prov:Activity ;
                    schema1:identifier "session-xrd-20260110-001" ;
                    schema1:location [ a <http://purl.org/nexusformat/definitions/BaseClass/NXsource>,
                                schema1:Place ;
                            schema1:identifier "https://ror.org/00hx57361" ;
                            schema1:name "Analytical Sciences Laboratory" ] ;
                    schema1:mainEntity [ a schema1:Thing,
                                <https://w3id.org/isample/vocabulary/materialsampleobjecttype/materialsample> ;
                            schema1:additionalType "MaterialSample" ;
                            schema1:description "Thin section of Allan Hills 84001 martian meteorite" ;
                            schema1:identifier "igsn:10.60471/GSEEXAMPLE001" ;
                            schema1:name "ALH 84001,123" ] ;
                    schema1:startDate "2026-01-10T09:30:00" ;
                    prov:used [ a <http://purl.org/nexusformat/definitions/BaseClass/NXinstrument>,
                                schema1:Thing,
                                prov:Entity ;
                            schema1:additionalType "ada:XRDInstrument" ;
                            schema1:identifier "ex:instrument-xrd-001" ;
                            schema1:name "Example XRD Instrument" ] ] ;
            sh:resultMessage "The generatedBy Activity must specify at least one resource that was used" ;
            sh:resultPath prov:used ;
            sh:resultSeverity sh:Violation ;
            sh:sourceConstraintComponent sh:OrConstraintComponent ;
            sh:sourceShape [ sh:message "The generatedBy Activity must specify at least one resource that was used" ;
                    sh:minCount 1 ;
                    sh:or ( [ sh:nodeKind sh:IRI ] [ sh:datatype xsd:string ;
                                sh:minLength 3 ] ) ;
                    sh:path prov:used ] ;
            sh:value [ a <http://purl.org/nexusformat/definitions/BaseClass/NXinstrument>,
                        schema1:Thing,
                        prov:Entity ;
                    schema1:additionalType "ada:XRDInstrument" ;
                    schema1:identifier "ex:instrument-xrd-001" ;
                    schema1:name "Example XRD Instrument" ] ] .
