[] a sh:ValidationReport ;
    sh:conforms false ;
    sh:result [ a sh:ValidationResult ;
            sh:focusNode <https://example.org/adaXRD-example-001> ;
            sh:resultMessage "XRD products must have a schema:additionalType matching a valid XRD type." ;
            sh:resultPath schema1:additionalType ;
            sh:resultSeverity sh:Violation ;
            sh:sourceConstraintComponent sh:OrConstraintComponent ;
            sh:sourceShape [ sh:message "XRD products must have a schema:additionalType matching a valid XRD type." ;
                    sh:minCount 1 ;
                    sh:or ( [ sh:hasValue "X-ray Diffraction (XRD) Tabular" ] [ sh:hasValue "X-ray diffraction" ] ) ;
                    sh:path schema1:additionalType ] ;
            sh:value "ada:DataDeliveryPackage" ] .

