@prefix rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#> .
@prefix schema1: <http://schema.org/> .
@prefix sh: <http://www.w3.org/ns/shacl#> .
@prefix xsd: <http://www.w3.org/2001/XMLSchema#> .

[] a sh:ValidationReport ;
    sh:conforms false ;
    sh:result [ a sh:ValidationResult ;
            sh:focusNode [ a schema1:PropertyValue ;
                    schema1:url "http://ogc.org/defs/g35408y" ;
                    schema1:value "g35408y" ] ;
            sh:resultMessage "Less than 1 values on [ rdf:type schema1:PropertyValue ; schema1:url Literal(\"http://ogc.org/defs/g35408y\") ; schema1:value Literal(\"g35408y\") ]->schema1:propertyID" ;
            sh:resultPath schema1:propertyID ;
            sh:resultSeverity sh:Violation ;
            sh:sourceConstraintComponent sh:MinCountConstraintComponent ;
            sh:sourceShape [ sh:minCount 1 ;
                    sh:or ( [ sh:nodeKind sh:IRI ] [ sh:datatype xsd:string ;
                                sh:minLength 5 ] [ sh:class schema1:DefinedTerm ] ) ;
                    sh:path schema1:propertyID ] ] .

