@prefix cdifd: <https://cdif.org/validation/0.1/shacl#> .
@prefix schema1: <http://schema.org/> .
@prefix sh: <http://www.w3.org/ns/shacl#> .
@prefix xsd: <http://www.w3.org/2001/XMLSchema#> .

[] a sh:ValidationReport ;
    sh:conforms false ;
    sh:result [ a sh:ValidationResult ;
            sh:focusNode [ a schema1:PropertyValue ;
                    schema1:propertyID "xas:collimation" ;
                    schema1:value "none" ] ;
            sh:resultMessage "a name for the additional property must be provided, and have a lenght of at least 5 characters." ;
            sh:resultPath schema1:name ;
            sh:resultSeverity sh:Violation ;
            sh:sourceConstraintComponent sh:MinCountConstraintComponent ;
            sh:sourceShape cdifd:additionalPropertyNameProperty ],
        [ a sh:ValidationResult ;
            sh:focusNode [ a schema1:PropertyValue ;
                    schema1:propertyID "nxs:Field/NXcrystal/reflection" ;
                    schema1:value "1,1,1" ] ;
            sh:resultMessage "a name for the additional property must be provided, and have a lenght of at least 5 characters." ;
            sh:resultPath schema1:name ;
            sh:resultSeverity sh:Violation ;
            sh:sourceConstraintComponent sh:MinCountConstraintComponent ;
            sh:sourceShape cdifd:additionalPropertyNameProperty ],
        [ a sh:ValidationResult ;
            sh:focusNode [ a schema1:PropertyValue ;
                    schema1:propertyID "nxs:Field/NXcrystal/type" ;
                    schema1:value "crystal type" ] ;
            sh:resultMessage "a name for the additional property must be provided, and have a lenght of at least 5 characters." ;
            sh:resultPath schema1:name ;
            sh:resultSeverity sh:Violation ;
            sh:sourceConstraintComponent sh:MinCountConstraintComponent ;
            sh:sourceShape cdifd:additionalPropertyNameProperty ],
        [ a sh:ValidationResult ;
            sh:focusNode [ a schema1:PropertyValue ;
                    schema1:propertyID "nxs:Field/NXcrystal/chemical_formula" ;
                    schema1:value "Si" ] ;
            sh:resultMessage "a name for the additional property must be provided, and have a lenght of at least 5 characters." ;
            sh:resultPath schema1:name ;
            sh:resultSeverity sh:Violation ;
            sh:sourceConstraintComponent sh:MinCountConstraintComponent ;
            sh:sourceShape cdifd:additionalPropertyNameProperty ],
        [ a sh:ValidationResult ;
            sh:focusNode [ a schema1:PropertyValue ;
                    schema1:propertyID "nxs:Field/NXcrystal/d_spacing" ;
                    schema1:unitText "Angstrom" ;
                    schema1:value "3.13550" ] ;
            sh:resultMessage "a name for the additional property must be provided, and have a lenght of at least 5 characters." ;
            sh:resultPath schema1:name ;
            sh:resultSeverity sh:Violation ;
            sh:sourceConstraintComponent sh:MinCountConstraintComponent ;
            sh:sourceShape cdifd:additionalPropertyNameProperty ] .

