@prefix xsd: <http://www.w3.org/2001/XMLSchema#> .

[] a sh:ValidationReport ;
    sh:conforms false ;
    sh:result [ a sh:ValidationResult ;
            sh:focusNode _:N9306ed654b364740a622bcd841e5adf5 ;
            sh:resultMessage "identifier rules. for an identifier, the property ID is expected to specify an identifier scheme.Get values from https://registry.identifiers.org/registry/ for interoperability",
                "identifier rules. the context of an identifier, the PropertyValue provides the identifier value string-- either as a schema:value or a url. The value can be either a full URI or identifier token part of URI if a url is provided. url value must be a resolvable URI that identifies the resource." ;
            sh:resultSeverity sh:Violation ;
            sh:sourceConstraintComponent sh:OrConstraintComponent ;
            sh:sourceShape cdifd:CDIFIdentifierShape ;
            sh:value _:N9306ed654b364740a622bcd841e5adf5 ],
        [ a sh:ValidationResult ;
            sh:focusNode _:N9306ed654b364740a622bcd841e5adf5 ;
            sh:resultMessage "Node Literal(\"CtYO\") does not conform to one or more shapes in [ sh:nodeKind sh:IRI ] , [ sh:datatype xsd:string ; sh:minLength Literal(\"5\", datatype=xsd:integer) ] , [ sh:class schema1:DefinedTerm ]" ;
            sh:resultPath schema1:propertyID ;
            sh:resultSeverity sh:Violation ;
            sh:sourceConstraintComponent sh:OrConstraintComponent ;
            sh:sourceShape [ sh:minCount 1 ;
                    sh:or ( [ sh:nodeKind sh:IRI ] [ sh:datatype xsd:string ;
                                sh:minLength 5 ] [ sh:class schema1:DefinedTerm ] ) ;
                    sh:path schema1:propertyID ] ;
            sh:value "CtYO" ] .

_:N9306ed654b364740a622bcd841e5adf5 a schema1:PropertyValue ;
    schema1:propertyID "CtYO" ;
    schema1:url "vX" .

